package LeetCodeEx;

import java.util.HashMap;
import java.util.Map;

public class _657 {
    public boolean judgeCircle(String moves) {
        if(moves==null) return true;
        char[] str=moves.toCharArray();
        HashMap<Character,Integer> map=new HashMap<>();
        map.put('U', 0);
        map.put('D', 0);
        map.put('L', 0);
        map.put('R', 0);
        for(int i=0;i<str.length;i++){
            map.put(str[i],map.get(str[i])+1);
        }
        for(Map.Entry<Character,Integer> entry:map.entrySet()){
            System.out.println(entry.getKey()+" "+entry.getValue());
        }
        if(!map.get('L').equals(map.get('R'))) return false;
        if(!map.get('U').equals(map.get('D'))) return false;
        return true;

    }

    public static void main(String[] args) {
        _657 machine=new _657();
        //boolean is =machine.judgeCircle("");
        boolean is =machine.judgeCircle("UD");
        System.out.println(is);
    }
}
